import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setEmail, setName, setSurname } from "../slices/userSlice";
import { Link } from "react-router-dom";

function SigninPage() {
  const user = useSelector((state) => state.user);
  const name = user.name;
  const surname = user.surname;
  const email = user.email;

  const dispatch = useDispatch();

  // State for validation errors
  const [errors, setErrors] = useState({
    name: "",
    surname: "",
    email: "",
  });

  useEffect(() => {
    // Reset the values and errors when the component mounts
    dispatch(setName(""));
    dispatch(setSurname(""));
    dispatch(setEmail(""));
    setErrors({
      name: "",
      surname: "",
      email: "",
    });
  }, [dispatch]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    // Update the corresponding state and clear validation error
    switch (name) {
      case "name":
        dispatch(setName(value));
        setErrors({ ...errors, name: "" });
        break;
      case "surname":
        dispatch(setSurname(value));
        setErrors({ ...errors, surname: "" });
        break;
      case "email":
        dispatch(setEmail(value));
        setErrors({ ...errors, email: "" });
        break;
      default:
        break;
    }
  };

  const validateInputs = () => {
    let isValid = true;
    const newErrors = { name: "", surname: "", email: "" };

    // Basic validation checks
    if (!name.trim()) {
      newErrors.name = "Name is required";
      isValid = false;
    }

    if (!surname.trim()) {
      newErrors.surname = "Surname is required";
      isValid = false;
    }

    if (!email.trim()) {
      newErrors.email = "Email is required";
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      newErrors.email = "Invalid email address";
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleLogin = () => {
    if (validateInputs()) {
      // Perform login action if inputs are valid
      // You can dispatch login action or navigate to the next page
      console.log("Login successful!");
    } else {
      // Handle invalid inputs
      console.log("Invalid inputs. Please check the form.");
    }
  };

  return (
    <div>
      <div>
        <h3>Sign In  Page</h3>
      </div>

      <div className="inputArea">
        <div>
          <p className="text">Name:</p>
        </div>
        <div>
          <input
            className="input"
            name="name"
            value={name}
            onChange={handleInputChange}
          />
          <span className="error">{errors.name}</span>
        </div>
      </div>

      <div className="inputArea">
        <div>
          <p className="text">Surname:</p>
        </div>
        <div>
          <input
            className="input"
            name="surname"
            value={surname}
            onChange={handleInputChange}
          />
          <span className="error">{errors.surname}</span>
        </div>
      </div>

      <div className="inputArea">
        <div>
          <p className="text">E-Mail:</p>
        </div>
        <div>
          <input
            className="input"
            name="email"
            value={email}
            onChange={handleInputChange}
          />
          <span className="error">{errors.email}</span>
        </div>
      </div>

      <div className="buttonAreaLogin">
        <Link to="/home">
          <button className="loginButton" onClick={handleLogin}>
            Login
          </button>
        </Link>
      </div>
    </div>
  );
}

export default SigninPage;
